import React, { useState } from 'react';

const CreateBill = () => {
  const [customer, setCustomer] = useState('');
  const [items, setItems] = useState([
    { id: 1, description: '', quantity: 1, price: 0 }
  ]);
  
  const addItem = () => {
    const newItem = {
      id: items.length + 1,
      description: '',
      quantity: 1,
      price: 0
    };
    setItems([...items, newItem]);
  };
  
  const updateItem = (id, field, value) => {
    const updatedItems = items.map(item => {
      if (item.id === id) {
        return { ...item, [field]: value };
      }
      return item;
    });
    setItems(updatedItems);
  };
  
  const removeItem = (id) => {
    const updatedItems = items.filter(item => item.id !== id);
    setItems(updatedItems);
  };
  
  const calculateSubtotal = () => {
    return items.reduce((total, item) => {
      return total + (item.quantity * item.price);
    }, 0);
  };
  
  const calculateTax = () => {
    return calculateSubtotal() * 0.18; // 18% GST
  };
  
  const calculateTotal = () => {
    return calculateSubtotal() + calculateTax();
  };

  return (
    <div className="p-6 dark:bg-gray-900">
      <h1 className="text-3xl font-bold mb-6 dark:text-white">Create Bill</h1>
      
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6 mb-6">
        <h2 className="text-xl font-semibold mb-4 dark:text-white">Customer Information</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-gray-700 dark:text-gray-300 mb-2">Customer Name</label>
            <input
              type="text"
              className="w-full p-2 border rounded dark:bg-gray-700 dark:border-gray-600 dark:text-white"
              value={customer}
              onChange={(e) => setCustomer(e.target.value)}
            />
          </div>
          <div>
            <label className="block text-gray-700 dark:text-gray-300 mb-2">Date</label>
            <input
              type="date"
              className="w-full p-2 border rounded dark:bg-gray-700 dark:border-gray-600 dark:text-white"
              defaultValue={new Date().toISOString().split('T')[0]}
            />
          </div>
        </div>
      </div>
      
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6 mb-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold dark:text-white">Items</h2>
          <button 
            className="bg-orange-600 text-white px-4 py-2 rounded hover:bg-orange-700"
            onClick={addItem}
          >
            Add Item
          </button>
        </div>
        
        <div className="overflow-x-auto">
          <table className="min-w-full">
            <thead>
              <tr className="border-b dark:border-gray-700">
                <th className="px-4 py-2 text-left dark:text-gray-300">Description</th>
                <th className="px-4 py-2 text-left dark:text-gray-300">Quantity</th>
                <th className="px-4 py-2 text-left dark:text-gray-300">Price</th>
                <th className="px-4 py-2 text-left dark:text-gray-300">Total</th>
                <th className="px-4 py-2 text-left dark:text-gray-300">Action</th>
              </tr>
            </thead>
            <tbody>
              {items.map((item) => (
                <tr key={item.id} className="border-b dark:border-gray-700">
                  <td className="px-4 py-2">
                    <input
                      type="text"
                      className="w-full p-2 border rounded dark:bg-gray-700 dark:border-gray-600 dark:text-white"
                      value={item.description}
                      onChange={(e) => updateItem(item.id, 'description', e.target.value)}
                    />
                  </td>
                  <td className="px-4 py-2">
                    <input
                      type="number"
                      className="w-full p-2 border rounded dark:bg-gray-700 dark:border-gray-600 dark:text-white"
                      value={item.quantity}
                      onChange={(e) => updateItem(item.id, 'quantity', parseInt(e.target.value))}
                      min="1"
                    />
                  </td>
                  <td className="px-4 py-2">
                    <input
                      type="number"
                      className="w-full p-2 border rounded dark:bg-gray-700 dark:border-gray-600 dark:text-white"
                      value={item.price}
                      onChange={(e) => updateItem(item.id, 'price', parseFloat(e.target.value))}
                      min="0"
                      step="0.01"
                    />
                  </td>
                   <td className="px-4 py-2 dark:text-white">
                    ₹{(item.quantity * item.price).toFixed(2)}
                  </td>
                  <td className="px-4 py-2">
                    <button 
                      className="text-red-600 dark:text-red-400 hover:text-red-900 dark:hover:text-red-300"
                      onClick={() => removeItem(item.id)}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        
        <div className="mt-6 border-t dark:border-gray-700 pt-4">
          <div className="flex justify-end">
            <div className="w-64">
              <div className="flex justify-between py-2">
                <span className="font-medium dark:text-white">Subtotal:</span>
                <span className="dark:text-white">₹{calculateSubtotal().toFixed(2)}</span>
              </div>
              <div className="flex justify-between py-2">
                <span className="font-medium dark:text-white">GST (18%):</span>
                <span className="dark:text-white">₹{calculateTax().toFixed(2)}</span>
              </div>
              <div className="flex justify-between py-2 border-t border-gray-300 dark:border-gray-700 mt-2 pt-2">
                <span className="font-bold dark:text-white">Total:</span>
                <span className="font-bold dark:text-white">₹{calculateTotal().toFixed(2)}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      <div className="flex justify-end mt-6">
        <button className="bg-gray-300 dark:bg-gray-700 text-gray-800 dark:text-white px-6 py-2 rounded hover:bg-gray-400 dark:hover:bg-gray-600 mr-4">
          Cancel
        </button>
        <button className="bg-orange-600 text-white px-6 py-2 rounded hover:bg-orange-700">
          Save Invoice
        </button>
      </div>
    </div>
  );
};

export default CreateBill;
